import { Fragment } from "react";

const About = () => {
  const about = [
    {
      id: 1,
      content:
        "Our system evaluates your skills across various stages, starting with education level, language proficiency, special talents, software knowledge, work experience, and more.",
      icon: <AboutFirstIcon />,
    },
    {
      id: 2,
      content:
        "By comparing you with people of the same age group, our system provides accurate results on your performance, highlighting your strengths and weaknesses.",
      icon: <AboutSecondIcon />,
    },
    {
      id: 3,
      content:
        "As you progress through each stage, you can learn more about yourself and the areas where you excel.",
      icon: <AboutThirdIcon />,
    },
  ];

  return (
    <Fragment>
      <ul className="py-20 grid gap-10">
        {about.map(({ id, content, icon }) => (
          <li key={id} className="flex items-center gap-8">
            <div className="text-[80px] w-16 text-transparent flex bg-gradient-to-b from-[#00404C] to-[#08D0B0] font-manrope font-medium bg-clip-text">
              {id}
            </div>
            <div className="px-10 w-full bg-white/30 box-shadow py-8 rounded-2xl border flex items-center border-qss-border gap-12">
              <p className=" text-[18px] text-justify">{content}</p>
              <div>{icon}</div>
            </div>
          </li>
        ))}
      </ul>
    </Fragment>
  );
};

